def update_quick_answers(file_path):
    """Update quick_answer fields in a JSON file"""
    with open(file_path, 'r') as f:
        data = json.load(f)

    updated_count = 0

    for subtopic in data.get('subtopic_pillars', []):
        for paa_post in subtopic.get('paa_posts', []):
            title = paa_post.get('title', '')
            if title in QUICK_ANSWERS:
                paa_post['quick_answer'] = QUICK_ANSWERS[title]
                updated_count += 1
            elif '[Provide' in paa_post.get('quick_answer', '') or '[2-3 sentence' in paa_post.get('quick_answer', ''):
                # Has placeholder text, needs update
                print(f"  Warning: No quick answer defined for: {title}")

    # Write back
    with open(file_path, 'w') as f:
        json.dump(data, f, indent=2)

    return updated_count

